/*** GENERAL ***/
.Colour.fullscreen {
  background-color: #101825 !important;
}

a {
  color: #1CA0FD !important;
}

hr {
  border-top: 2px solid #3B6A87 !important;
}

button {
  border-radius 2em;
  cursor: pointer;
  display: inline-block;
  padding: 0.5em 1em;
  text-decoration: none;
  font-weight: 500;
  background-color: #58B8FD;
  color: #1A283F;
  border: 1px solid #3AACFD;
  border-radius: 4px;
  transition: background-color 0.25s ease-out;
}

button:hover {
  background-color: #1A283F;
  color: #58B8FD;
}

select {
  color: #D0E2F0 !important;
  background-color: #22385C !important;
  border-color: #3AACFD !important;
}

select.primary {
  border: 2px solid #1CA0FD !important;
}

input {
  color: #D0E2F0 !important;
  background-color: #22385C !important;
  border-color: #3AACFD !important;
}

label {
  color: #F8B471;
}

textarea {
  background-color: #1A283F;
  color: #D0E2F0 !important;
  border: 1px solid #3AACFD !important;
}

p {
  color: #D0E2F0;
}

.info {
  color: #8FD1C7;
}

/*** Overlay for Settings ***/
.Overlay:hover {
  background-color: rgba(26, 40, 63, 0.65);
}

/* Loading Icon */
.Overlay span i {
  color: #F0E68C;
}

/*** Settings ***/
.Settings .plane {
  background-color: #1A283F;
  width: 370px;
}

.Settings h1, .Settings h2, .Settings h3, .Settings h4, .Settings h5, .Settings h6 {
  color: #D0E2F0;
}


.Settings .Widget {
  background-color: #22385C;
}

.Settings .Widget select, .Settings .Widget input {
  background-color: #1A283F !important;
}

.Settings .Widget hr {
  border: 1px solid #3B6A87 !important;
}


.Settings .Widget .button--icon {
  background-color: #1A283F;
  color: #1CA0FD;
}

.Settings .Widget .button--icon:hover {
  background-color: #1CA0FD !important;
  color: #1A283F !important;
}

.Settings .Widget .button--primary {
  background-color: #1CA0FD !important;
  color: #1A283F !important;
}

.Settings .Widget .button--primary:hover {
  background-color: #1A283F !important;
  color: #1CA0FD !important;
}

/*** WEATHER ***/
.Weather {
  color: #94D0FE !important;
}

.Weather .temperature {
  color: #8FD1C7;
}

/*** IP INFO ***/
.IpInfo {
  color: #8FD1C7;
}

/*** CLOCK ***/
/* Main */
.Time {
  color: #58B8FD !important;
}
/* Outer */
.Time.Analogue .theme-stroke {
  stroke: #1CA0FD
}
/* Middle */
.Time.Analogue .theme-fill {
  fill: #1A283F;
}

/* Time */
.Time.Analogue line.hours {
  stroke: #58B8FD;
}
.Time.Analogue line.minutes {
  stroke: #58B8FD;
}
.Time.Analogue line.seconds {
  stroke: #F8B471;
}

/*** QUICK LINKS ***/
.Links {
  column-gap: 0.25em;
}

.Links a {
  padding: 0.25em 0.5em;
  background: #1A283F;
  border-radius: 4px;
  text-align: center;
}

.Links i {
  display: inline-block;
}

.Links .LinkText {
  color: #94D0FE;
}

/*** Icons ***/
.Links a i, .Weather i {
  color: #F8B471;
}
